
public class Javaloops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=0;
		
		// while loop
		while(i<10) {
			System.out.println(i);
		    i=i+1;
		}
		
		int j=10;
		  while(j>0) {
			System.out.println(j);
		    j=j-1;
		  }
		
		// For loop
		   for(int x=0;x<5;x++){
			   System.out.println(x);
		   }
		// for loop
		   
		   for(int a=0;a<5;a++) {
			   for(int b=1;b<6-a;b++) {
		   
		   
           System.out.print(b);}
			   System.out.println();
           
          
			   }
		   
			   
		   }
}
	
	
	
	


